class Solution {
public:
    vector<int> getConcatenation(vector<int>& nums) {
        int len=nums.size();
        vector<int> ans(len*2);
        for(int i=0; i<len; i++){
            ans[i]=nums[i];
            ans[len+i]=nums[i];
        }
        return ans;
    }
};

class Solution {
public:
    vector<int> plusOne(vector<int>& digits) {
        int len=digits.size();
        for(int i=len-1; i>=0; i--){
            if(digits[i]!=9){
                digits[i]+=1;
                return digits;
            }
            else
            {
                if(i==0){
                    digits[i]=1;
                    digits.push_back(0);
                }
                else digits[i]=0;
            }
        }
        return digits;
    }
};
